$(document).ready(function(){

    $('.datetimepicker').datetimepicker({
        locale:'vi',
        format: 'DD-MM-YYYY HH:mm'
    });
    $('.datetimepicker-timeonly').datetimepicker({
        locale:'vi',
        format: 'LT'
    });
    $('.datepicker').datetimepicker({
        locale:'vi',
        format: 'DD-MM-YYYY'
    })
});


